function fetchTicket(){fetch("https://api.2nite.ro/api/v2/tickets/blueprints/t/"+TICKET).then((t=>t.json())).then((t=>{const e=t.data;document.getElementById("title").textContent=e.attributes.name,document.getElementById("description").textContent=e.attributes.description,document.getElementById("price").textContent=e.attributes.price/100+"RON"})).catch((t=>console.error(t)))}function buyTicket(){window.location.href="https://2nite.ro/events/"+EVENT}function incrementCounter(){const t=document.querySelector(".counter-value");let e=parseInt(t.textContent);t.textContent=e+1}function decrementCounter(){const t=document.querySelector(".counter-value");let e=parseInt(t.textContent);e>0&&(t.textContent=e-1)}window.addEventListener("load",(()=>{fetchTicket(),document.querySelector(".increment-button").addEventListener("click",incrementCounter),document.querySelector(".decrement-button").addEventListener("click",decrementCounter)}));